mapper.select:
SELECT COUNT(*)
FROM customers c
WHERE c.user_id = #{userId}

SELECT COUNT(*)
FROM customers c
         JOIN users u ON u.user_id = c.user_id
WHERE c.customer_id = #{customerId}
  AND u.center_id = #{centerId}

SELECT COUNT(*)
FROM customers
WHERE customer_id = #{customerId}
  AND user_id = #{userId}

mapper.update:
UPDATE customers
SET customer_promise_time = #{when}
WHERE customer_id = #{customerId}

UPDATE customers
SET customer_status = #{status}
WHERE customer_id = #{customerId}
