mapper.select:
SELECT t.id, t.origin, t.createdAt, t.staff, t.division, t.category, t.name, t.phone, t.source, t.content, t.memo, t.status, t.reservation FROM ( /* ===== customers 분기 ===== */ SELECT c.customer_id AS id, 'CUSTOMER' AS origin, c.customer_created_at AS createdAt, u.user_name AS staff, c.customer_division AS division, c.customer_category AS category, c.customer_name AS name, c.customer_phone AS phone, c.customer_source AS source, c.customer_content AS content, c.customer_memo AS memo, c.customer_status AS status, c.customer_promise_time AS promiseAt, DATE_FORMAT(c.customer_promise_time, '%c월 %e일 %H:%i') AS reservation FROM customers c LEFT JOIN users u ON u.user_id = c.user_id WHERE 1=1
AND c.customer_created_at >= #{dateFrom}
AND c.customer_created_at < DATE_ADD(#{dateTo}, INTERVAL 1 DAY)
AND c.customer_division = #{division}
AND c.customer_status = #{status}
UNION ALL
/* ===== customers_duplicate 분기 ===== */ SELECT d.duplicate_id AS id, 'DUPLICATE' AS origin, d.duplicate_created_at AS createdAt, u2.user_name AS staff, '중복' AS division, d.duplicate_category AS category, d.duplicate_name AS name, c2.customer_phone AS phone, d.duplicate_source AS source, d.duplicate_content AS content, d.duplicate_memo AS memo, '없음' AS status, NULL AS promiseAt, NULL AS reservation FROM customers_duplicate d JOIN customers c2 ON c2.customer_id = d.customer_id LEFT JOIN users u2 ON u2.user_id = c2.user_id WHERE d.duplicate_display = 1
AND d.duplicate_created_at >= #{dateFrom}
AND d.duplicate_created_at < DATE_ADD(#{dateTo}, INTERVAL 1 DAY)
) t WHERE 1=1
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM ( /* ===== customers 분기 ===== */ SELECT c.customer_id AS id, 'CUSTOMER' AS origin, c.customer_created_at AS createdAt, u.user_name AS staff, c.customer_division AS division, c.customer_category AS category, c.customer_name AS name, c.customer_phone AS phone, c.customer_source AS source, c.customer_content AS content, c.customer_memo AS memo, c.customer_status AS status, c.customer_promise_time AS promiseAt, DATE_FORMAT(c.customer_promise_time, '%c월 %e일 %H:%i') AS reservation FROM customers c LEFT JOIN users u ON u.user_id = c.user_id WHERE 1=1
AND c.customer_created_at >= #{dateFrom}
AND c.customer_created_at < DATE_ADD(#{dateTo}, INTERVAL 1 DAY)
AND c.customer_division = #{division}
AND c.customer_status = #{status}
UNION ALL
/* ===== customers_duplicate 분기 ===== */ SELECT d.duplicate_id AS id, 'DUPLICATE' AS origin, d.duplicate_created_at AS createdAt, u2.user_name AS staff, '중복' AS division, d.duplicate_category AS category, d.duplicate_name AS name, c2.customer_phone AS phone, d.duplicate_source AS source, d.duplicate_content AS content, d.duplicate_memo AS memo, '없음' AS status, NULL AS promiseAt, CAST(NULL AS CHAR(20)) AS reservation FROM customers_duplicate d JOIN customers c2 ON c2.customer_id = d.customer_id LEFT JOIN users u2 ON u2.user_id = c2.user_id WHERE d.duplicate_display = 1
AND d.duplicate_created_at >= #{dateFrom}
AND d.duplicate_created_at < DATE_ADD(#{dateTo}, INTERVAL 1 DAY)
) t WHERE 1=1

SELECT c.customer_id AS id, 'CUSTOMER' AS origin, c.customer_created_at AS createdAt, u.user_name AS staff, c.customer_division AS division, c.customer_category AS category, c.customer_name AS name, c.customer_phone AS phone, c.customer_source AS source, c.customer_content AS content, c.customer_memo AS memo, c.customer_status AS status, DATE_FORMAT(c.customer_promise_time, '%c월 %e일 %H:%i') AS reservation FROM customers c JOIN users u ON u.user_id = c.user_id
WHERE c.user_id IN (SELECT user_id FROM users WHERE center_id = #{centerId}) AND c.customer_status != '없음'
LIMIT #{size} OFFSET #{offset}

SELECT c.customer_id AS id, 'CUSTOMER' AS origin, c.customer_created_at AS createdAt, u.user_name AS staff, c.customer_division AS division, c.customer_category AS category, c.customer_name AS name, c.customer_phone AS phone, c.customer_source AS source, c.customer_content AS content, c.customer_memo AS memo, c.customer_status AS status, DATE_FORMAT(c.customer_promise_time, '%c월 %e일 %H:%i') AS reservation FROM customers c LEFT JOIN users u ON u.user_id = c.user_id
WHERE c.user_id = #{userId} AND c.customer_status != '없음'
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM customers c
         JOIN users u ON u.user_id = c.user_id
WHERE c.customer_id = #{customerId}
  AND u.center_id = #{centerId}

SELECT COUNT(*)
FROM customers
WHERE customer_id = #{customerId}
  AND user_id = #{userId}

mapper.update:
UPDATE customers
SET customer_promise_time = #{when}
WHERE customer_id = #{customerId}

UPDATE customers
SET customer_status = #{status}
WHERE customer_id = #{customerId}
