mapper.select:
SELECT c.customer_id AS id, c.customer_name AS name, CASE WHEN #{visible} = 'Y' THEN c.customer_phone ELSE CONCAT(SUBSTRING_INDEX(c.customer_phone,'-',1), '-****-', SUBSTRING_INDEX(c.customer_phone,'-',-1)) END AS phone, u.user_name AS staff, c.customer_division AS division, c.customer_category AS category, c.customer_status AS status, ctr.center_name AS centerName, c.customer_source AS source, c.customer_content AS content, c.customer_created_at AS createdAt FROM customers c LEFT JOIN users u ON u.user_id = c.user_id LEFT JOIN centers ctr ON ctr.center_id = u.center_id
WHERE c.customer_status != '회수' AND c.user_id IS NOT NULL
AND c.customer_division = #{division}
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM customers c LEFT JOIN users u ON u.user_id = c.user_id
WHERE c.customer_status != '회수' AND c.user_id IS NOT NULL
AND c.customer_division = #{division}

SELECT c.customer_id AS id, c.customer_name AS name, c.customer_phone AS phone, u.user_name AS staff, c.customer_division AS division, c.customer_category AS category, c.customer_status AS status, (SELECT center_name FROM centers WHERE center_id = u.center_id) AS centerName, c.customer_source AS source, c.customer_content AS content, c.customer_created_at AS createdAt FROM customers c JOIN users u ON u.user_id = c.user_id
WHERE c.customer_status != '회수' AND c.user_id IS NOT NULL AND u.center_id = #{managerCenterId}
AND c.user_id != #{excludeUserId}
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM customers c JOIN users u ON u.user_id = c.user_id
WHERE c.customer_status != '회수' AND c.user_id IS NOT NULL AND u.center_id = #{managerCenterId}
AND c.user_id != #{excludeUserId}

SELECT c.customer_id FROM customers c WHERE c.customer_id IN #{id} AND c.customer_status != '회수' AND c.user_id IS NOT NULL AND EXISTS ( SELECT 1 FROM users u WHERE u.user_id = c.user_id AND u.center_id = #{managerCenterId} )
AND c.user_id != #{excludeUserId}
FOR UPDATE
