mapper.update:
UPDATE users
SET user_phone = #{userPhone}
WHERE user_email = #{userEmail}

UPDATE users
SET user_password = #{userPassword}
WHERE user_email = #{userEmail}

mapper.select:
SELECT EXISTS(
    SELECT 1 FROM centers WHERE center_name = #{centerName} LIMIT 1
)

SELECT COUNT(*)
FROM users
WHERE center_id = #{centerId}
