mapper.update:
UPDATE gsheet_sources
SET cursor_row = CASE
                     WHEN cursor_row < #{newCursor} THEN #{newCursor}
                     ELSE cursor_row
    END
WHERE source_id = #{sourceId}

mapper.select:
SELECT customer_id
FROM customers
WHERE customer_phone = #{phone}
  AND customer_created_at >= #{threshold}
ORDER BY customer_created_at DESC
LIMIT 1

SELECT CASE WHEN EXISTS (
    SELECT 1 FROM customers WHERE customer_phone = #{phone}
) THEN TRUE ELSE FALSE END
