mapper.select:
SELECT u.user_id AS userId, u.user_name AS userName, u.user_phone AS userPhone, u.user_email AS userEmail, u.user_role AS userRole, u.user_approved AS userApproved, u.manager_phone_access AS managerPhoneAccess, c.center_name AS centerName FROM users u LEFT JOIN centers c ON u.center_id = c.center_id
AND (u.user_name LIKE CONCAT('%', #{keyword}, '%') OR u.user_email LIKE CONCAT('%', #{keyword}, '%') OR u.user_phone LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY u.user_created_at DESC LIMIT #{size} OFFSET #{offset}
